import React, { createContext, useState } from "react"
import MyRecipes from "./MyRecipes";
import { dataBaseService } from '../../Backend/FirebaseUtils';

export const RecipeContext=createContext();

export function RecipeProvider({children}){
  const [recipes,setRecipe]=useState([])
  const[myRecipes,setMyRecipes]=useState([])

  const addRecipe=(data)=>{
      const recipes=Promise.resolve(data)
      recipes.then(value=>{
        setRecipe(value)
        console.log(value)
      }).catch(err=>{
        console.log(err)
      })
  }

  const getMyRecipes=()=>{
    const getRecipes=Promise.resolve(dataBaseService.get);
    getRecipes.then(value=>{
      value.then((QuerySnapshot) =>{
        setMyRecipes(QuerySnapshot.docs.map((doc) =>(doc.data())))
    })
    console.log(myRecipes)
      ;
    }).catch(err=>{
      console.log(err)
    })
  
  }

  return(
    <RecipeContext.Provider value={{
      recipes:recipes,addRecipe,myRecipes:myRecipes,getMyRecipes
    }}>
        {children}
    </RecipeContext.Provider>
  )
}
